import React from 'react'
import './tab.css'

export default class Tab extends React.Component{
  changeClass = (idx) => {
    console.log(idx.detail)
  } 
  render(){
    return(
      <div className='Tab'>
        <div className='tab-box '>
          <div className='directory tab-common high-light' tabIndex='1' onClick={this.changeClass.bind(this)}>目录</div>
          <div className='evaluation tab-common' tabIndex='2'>评价<span>(999)</span></div>
          <div className='recommend tab-common' tabIndex='3'>推荐</div>
        </div>
        <div className='tab-directory'></div>
      </div>
    )
  }
}
