import React from 'react'
import Swiper from 'swiper';
import 'swiper/swiper-bundle.css'

export default class SwiperPra extends React.Component{

  componentDidMount() {
    new Swiper('.swiper-container', {
      initialSlide: 0,
      direction: 'horizontal',
      grabCursor: true,
      autoplay: true,
      loop: true,
      observer:true, 
      observeParents:true,
    })

  }
  render(){
    return(
      <div className='Swiper'>
        <div className='swiper-container'>
          <div className='swiper-wrapper'>
            <div className='swiper-slide'>
              <div style={{width:'200px',height:'200px',background:'pink'}}>1</div>
            </div>
            <div className='swiper-slide'>
              <div style={{width:'200px',height:'200px',background:'red'}}>2</div>
            </div>
            <div className='swiper-slide'>
              <div style={{width:'200px',height:'200px',background:'blue'}}>3</div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
