import React from 'react'
import './direct-content.css'
import PropTypes from 'prop-types'

export default class DirectContent extends React.Component {
  static defaultProps = {
    attribute: 0,
    sectionTitle: '',
    teacherName: '',
    partNum: '',
    liveTime: '',
    playNum: '',
    studyProgress: '',
    studyStatus: '',
    videoTime: ''
  }
  render() {
    const { directData = [] } = this.props
    return (
      <div className='DirectContent'>
        {
          directData.map((item, index) => {
            const { attribute, sectionTitle, teacherName, partNum, liveTime, playNum, studyProgress, studyStatus, videoTime } = item
            return (
              <div key={index} className='content-container'>
                <div className='lesson-attr-container'>
                  {attribute === 1 ? <div className='live attr-common '>LIVE</div>
                    : attribute === 2 ? <div className='trailer attr-common '>预告</div>
                      : attribute === 3 ? <div className='play attr-common '>1</div>
                        : attribute === 4 ? <div className='studied attr-common '>2</div>
                          : ''
                  }
                </div>
                <div className='lesson-info-container'></div>
              </div>

            )
          })
        }
      </div>
    )
  }
}
