import React from 'react'
import './direct-content.css'
import PropTypes from 'prop-types'


export default class DirectContent extends React.Component {
  static defaultProps = {
    attribute: 0,
    sectionTitle: '',
    teacherName: '',
    partNum: '',
    liveTime: '',
    playNum: '',
    studyProgress: '',
    studyStatus: '',
    videoTime: ''
  }
  renderSubLive = (partNum, liveTime, playNum, studyProgress, studyStatus) => {
    let emptyArr = []
    if (!!partNum) {
      emptyArr.push(<div className='part-num'>{partNum}人参与</div>)
    }
    if (!!liveTime) {
      emptyArr.push(<div className='live-time'>{liveTime}直播</div>)
    }
    if (!!playNum) {
      emptyArr.push(<div className='play-num'>{playNum}次播放</div>)
    }
    if (!!studyProgress) {
      emptyArr.push(<div className='study-progress'>已学{studyProgress}</div>)
    }
    if (!!studyStatus) {
      emptyArr.push(<div className='study-status'>{studyStatus}</div>)
    }
    return emptyArr

  }
  getEveryPageArr = (directData = [], totalPage) => {
    let outerArr = []
    let innerArr = []
    for (let i = 0; i < totalPage; i++) {
      for (let j = i; j< directData.length;j++){
        innerArr.push(directData.splice(j,1))
        if(directData.length === 1){
          innerArr.push(directData[0])
        }

      }
      outerArr.push(innerArr)

    }
    return outerArr
  }
  render() {
    const { directData = [] } = this.props
    const totalPage = parseInt(Math.ceil(directData.length / 5))
    const newPageArr=this.getEveryPageArr(directData, totalPage)
    return (
      <div className='DirectContent'>
        {
          directData.map((item, index) => {
            const { attribute, sectionTitle, teacherName, partNum, liveTime, playNum, studyProgress, studyStatus, videoTime } = item
            return (
              <div key={index} className='content-container'>
                <div className='lesson-attr-container'>
                  {attribute === 1 ? <div className='live attr-common '></div>
                    : attribute === 2 ? <div className='trailer attr-common '></div>
                      : attribute === 3 ? <div className='attr-play attr-common '></div>
                        : attribute === 4 ? <div className='studied attr-common '></div>
                          : ''
                  }
                  {!!videoTime ? <div className='video-time'>{videoTime}</div> : ''}
                  <div className='section-title'>{index + 1}.{sectionTitle}</div>
                </div>
                <div className='lesson-info-container'>
                  <div className='teacher-name'>{teacherName}</div>
                  {this.renderSubLive(partNum, liveTime, playNum, studyProgress, studyStatus)}
                </div>
              </div>

            )
          })
        }
        <div>{newPageArr.length}</div>
      </div>
    )
  }
}
